import inquirer from 'inquirer';

// Generate a random number between min and max
const generateRandomNumber = (min: number, max: number): number => {
    return Math.floor(Math.random() * (max - min + 1)) + min;
};

// Main function to run the game
const playGame = async () => {
    const min = 1;
    const max = 10;
    const randomNumber = generateRandomNumber(min, max);
    console.log(`Guess the number between ${min} and ${max}:`);

    const answers = await inquirer.prompt([
        {
            type: 'input',
            name: 'userGuess',
            message: 'Your guess:',
            validate: (input: string) => {
                const number = parseInt(input, 10);
                if (isNaN(number) || number < min || number > max) {
                    return `Please enter a number between ${min} and ${max}.`;
                }
                return true;
            }
        }
    ]);

    const userGuess = parseInt(answers.userGuess, 10);

    if (userGuess === randomNumber) {
        console.log('Congratulations! You guessed the number correctly!');
    } else {
        console.log(`Sorry, the correct number was ${randomNumber}. Better luck next time!`);
    }
};

// Run the game
playGame();
